import React, { useEffect, useState } from "react";
import "./HomeScreen.style.css";
import Header from "../../components/Header/Header";
import FolderDialog from "../../components/FolderDialog/FolderDialog";
import Canvas from "../../components/canvas/Canvas";
import Footer from "../../components/footer/Footer";
import Button from "../../components/buttons/Button";
import HomeScreenText from "../../components/text/HomeScreenText";
import axios from "axios";

class HomeScreen extends React.Component {
  // const getData = async () => {
  // const response = await fetch("http://127.0.0.1:5000/", {
  // mode: "no-cors",
  // method: "GET",
  // });
  // const resData = await response;
  // console.log(resData, "damnn");
  // };

  // useEffect(() => {
  // getData();
  // console.log("hi");
  // }, []);

  constructor(props) {
    super(props);
    this.state = {
      arrays: [],
      clicked: false,
    };

    this.changeArrays = this.changeArrays.bind(this);
    this.handleClick = this.handleClick.bind(this);
  }

  changeArrays(new_arrays) {
    this.setState({ arrays: new_arrays });
    console.log(this.state.arrays);
  }
  handleClick() {
    this.setState({
      clicked: true,
    });
  }

  render() {
    return (
      <body>
        {/* <Header /> */}
        {/* <HomeScreenText /> */}

        <div styles={{ padding: "0px" }}>
          <div class="centered">
            <Button title="Show Folder Dialog" onClick={this.handleClick} />

            {this.state.clicked ? (
              <FolderDialog changeArrays={this.changeArrays} />
            ) : null}
          </div>
        </div>
        <div class="centered">
          <Button title="Show Visualizer" onClick={this.handleClick} />

          {this.state.clicked ? <Canvas data={this.state.arrays} /> : null}
        </div>
        <Footer />
      </body>
    );
  }
}

export default HomeScreen;
