import React from "react";
import "./HomeScreen.style.css";
import Header from "../../components/Header/Header";
import FolderDialog from "../../components/FolderDialog/FolderDialog";
import Canvas from "../../components/canvas/Canvas";
import Footer from "../../components/footer/Footer";

function HomeScreen() {
  return (
    <body>
      <Header />
      <p>
        This web-app is an educational tool that creates visualizations of audio
        files based on the Nyquist-Shannon Theorum. All you gotta do is upload a
        .wav file below.{" "}
      </p>
      <div class="container">
        <FolderDialog />
      </div>
      <div class="container">
        <Canvas />
      </div>
      <Footer />
    </body>
  );
}

export default HomeScreen;
