import React, { useState } from "react";

const FolderDialog = () => {
  const [selectedFile, setSelectedFile] = useState();
  const [isFilePicked, setIsFilePicked] = useState(false);

  const changeHandler = (event) => {
    setSelectedFile(event.target.files[0]);
    setIsFilePicked(true);
  };

  const handleSubmission = () => {
    console.log("Clicked");
  };

  return (
    <div class="container">
      <div class="card ">
        <input type="file" name="file" onChange={changeHandler} />
        <div>
          <button class="button" onClick={handleSubmission}>
            Submit
          </button>
        </div>
        <div>
          <img
            class="image"
            src="https://images.unsplash.com/photo-1617994452722-4145e196248b?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=2940&q=80"
          ></img>
        </div>
      </div>
    </div>
  );
};

export default FolderDialog;
