import React, { useState } from "react";
import "./FolderDialog.style.css";
import { soundWave } from "../../../public";

const FolderDialog = () => {
  const [selectedFile, setSelectedFile] = useState();
  const [isFilePicked, setIsFilePicked] = useState(false);

  const changeHandler = (event) => {
    setSelectedFile(event.target.files[0]);
    setIsFilePicked(true);
  };

  const handleSubmission = () => {
    console.log("Clicked");
  };

  return (
    <div class="card" style={{ width: "18rem" }}>
      <img
        class="card-img-top"
        src="/Users/kevin/Desktop/nyquist-shannon/public/720906511-vector.eps"
        alt=""
      />
      <div class="card-body">
        <h5 class="card-title">Select File:</h5>
        <p class="card-text">
          Choose the sound file you want to run through the visualizer.
        </p>
        <input type="file" name="file" onChange={changeHandler} />
        <div>
          <button class="button" onClick={handleSubmission}>
            Submit
          </button>
        </div>
      </div>
    </div>
  );
};
export default FolderDialog;
