import React, { useState, useEffect } from "react";
import "react-h5-audio-player/lib/styles.css";
import { WaveFile } from "wavefile";
import fs from "fs";

class PlayAudio extends React.Component {
  constructor(props) {
    super(props);
  }

  render() {
    let wav = new WaveFile(this.props.processed);
    wav.toSampleRate(32000);
    fs.writeFileSyinc("32000Hz-file.wav", wav.toBuffer());

    return (
      <div>
        <div class="originalAudio">
          <p>This is the original.</p>
          {this.props.audio && (
            <audio
              controls
              src={"data:audio/wav;base64," + this.props.audio}
            ></audio>
          )}
        </div>
        <div class="compressedAudio">
          <p>This is the processed audio.</p>
          {this.props.audio && (
            <audio controls src={"32000Hz-file.wav"}></audio>
          )}
        </div>
      </div>
    );
  }
}

export default PlayAudio;
