import React, { useState } from "react";
import "./FolderDialog.style.css";
import Button from "../buttons/Button";

import SoundWave from "../SoundWave";

const FolderDialog = () => {
  const [selectedFile, setSelectedFile] = useState();
  const [isFilePicked, setIsFilePicked] = useState(false);

  const changeHandler = (event) => {
    setSelectedFile(event.target.files[0]);
    setIsFilePicked(true);
  };

  const handleSubmission = () => {
    const formData = new formData();

    formData.append("File", selectedFile);
    fetch("https://freeimage.host/api/1/upload?key=<YOUR_API_KEY>", {
      method: "POST",
      body: formData,
    })
      .then((response) => response.json())
      .then((result) => {
        console.log("Success:", result);
      })
      .catch((error) => {
        console.error("Error:", error);
      });
  };

  return (
    <div class="cards">
      <div>
        <h5>Select File:</h5>
        <p>Choose the sound file you want to run through the visualizer.</p>
        {isFilePicked && <SoundWave />}
        {!isFilePicked ? (
          <input type="file" name="file" onChange={changeHandler} />
        ) : (
          <Button title="Convert File" onClick={handleSubmission} />
        )}
      </div>
    </div>
  );
};
export default FolderDialog;
